import java.lang.*;

public class adjacent13 {

	public static void main(String[] args) {
		String sequence = "";
		int sequenceLength = 999;
		final int atatime = 13;
		String substr;
		long maxvalue = 1;
		for( int i = 0; i < ( sequenceLength - atatime ); i++) {
			substr = sequence.substring(i,i + atatime);
			long number = Long.parseLong(substr);
			long multiplicand = 1;
			for ( ; number > 0; number/= 10 ) {
				multiplicand *= number%10;
			}
			if(multiplicand > maxvalue) {
				maxvalue = multiplicand;
			}
		}
	    System.out.println(maxvalue);
	}

}
